		} // if ($deep)

		$this->collChips = null;
		$this->collInventarios = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseEstado:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseEstado::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


